mapper.insert:

    SELECT BOARD_NO_SEQ.NEXTVAL FROM DUAL

/* 게시글 작성 */
INSERT INTO BOARD_INFO
    (
        BOARD_NO
      , TITLE
      , CONTENT
      , WRITER
    )
VALUES
    (
        #{boardNo}
      , #{title}
      , #{content}
      , #{writer}
    )

mapper.update:
/* 게시글 수정 */
UPDATE BOARD_INFO
   SET TITLE = #{title}
     , CONTENT = #{content}
     , MOD_DATE = SYSDATE
 WHERE BOARD_NO = #{boardNo}

/* 게시글 삭제 */
UPDATE BOARD_INFO
   SET USE_YN = 'N'
 WHERE BOARD_NO = #{boardNo}
