mapper.select:
SELECT COUNT(*)
  FROM REPLY_INFO
 WHERE BOARD_NO = #{boardNo}

mapper.insert:

    SELECT REPLY_NO_SEQ.NEXTVAL FROM DUAL

/* 댓글 작성 */
INSERT INTO REPLY_INFO
    (
        REPLY_NO
      , BOARD_NO
      , REPLY_CONTENT
      , REPLY_WRITER
      , REG_DATE
    )
VALUES
    (
        #{replyNo}
      , #{boardNo}
      , #{replyContent}
      , #{replyWriter}
      , SYSDATE
    )

mapper.update:
/* 댓글 수정 */
UPDATE REPLY_INFO
   SET REPLY_CONTENT = #{replyContent}
     , MOD_DATE = SYSDATE
 WHERE REPLY_NO = #{replyNo}
   AND BOARD_NO = #{boardNo}

/* 댓글 삭제 */
UPDATE REPLY_INFO
   SET DEL_YN = 'Y'
 WHERE REPLY_NO = #{replyNo}
   AND BOARD_NO = #{boardNo}
